'use strict';
module.exports = function localize_ru(errors) {
  if (!(errors && errors.length)) return;
  for (var i = 0; i < errors.length; i++) {
    var e = errors[i];
    var out;
    switch (e.keyword) {
      case '$ref':
        out = 'не найдена схема ' + (e.params.escaped);
        break;
      case 'additionalItems':
        out = '';
        var n = e.schema.length;
        out += 'должен иметь не более, чем ' + (n) + ' элемент';
        if (n >= 2 && n <= 4) {
          out += 'а';
        } else if (n != 1) {
          out += 'ов';
        }
        break;
      case 'additionalProperties':
        out = 'не должен иметь дополнительные поля';
        break;
      case 'anyOf':
        out = 'должен соответствовать одной их схем в "anyOf"';
        break;
      case 'dependencies':
        out = '';
        var n = e.params.n;
        out += 'должен иметь пол';
        if (n == 1) {
          out += 'е';
        } else {
          out += 'я';
        }
        out += ' ' + (e.params.deps) + ', когда присутствует поле ' + (e.params.property);
        break;
      case 'enum':
        out = 'должен быть равен одному из значений в "enum"';
        break;
      case 'format':
        out = 'должен соответствовать формату "' + (e.params.escaped) + '"';
        break;
      case 'maxItems':
        out = '';
        var n = e.schema;
        out += 'должен иметь не более, чем ' + (n) + ' элемент';
        if (n >= 2 && n <= 4) {
          out += 'а';
        } else if (n != 1) {
          out += 'ов';
        }
        break;
      case 'maxLength':
        out = '';
        var n = e.schema;
        out += 'должен быть не длиннее, чем ' + (n) + ' символ';
        if (n >= 2 && n <= 4) {
          out += 'а';
        } else if (n != 1) {
          out += 'ов';
        }
        break;
      case 'maxProperties':
        out = '';
        var n = e.schema;
        out += 'должен иметь не более, чем ' + (n) + ' пол';
        if (n == 1) {
          out += 'е';
        } else if (n >= 2 && n <= 4) {
          out += 'я';
        } else {
          out += 'ей';
        }
        break;
      case 'maximum':
        out = 'должен быть ' + (e.params.condition);
        break;
      case 'minItems':
        out = '';
        var n = e.schema;
        out += 'должен иметь не менее, чем ' + (n) + ' элемент';
        if (n >= 2 && n <= 4) {
          out += 'а';
        } else if (n != 1) {
          out += 'ов';
        }
        break;
      case 'minLength':
        out = '';
        var n = e.schema;
        out += 'должен быть не короче, чем ' + (n) + ' символ';
        if (n >= 2 && n <= 4) {
          out += 'а';
        } else if (n != 1) {
          out += 'ов';
        }
        break;
      case 'minProperties':
        out = '';
        var n = e.schema;
        out += 'должен иметь не менее, чем ' + (n) + ' пол';
        if (n == 1) {
          out += 'е';
        } else if (n >= 2 && n <= 4) {
          out += 'я';
        } else {
          out += 'ей';
        }
        break;
      case 'minimum':
        out = 'должен быть ' + (e.params.condition);
        break;
      case 'multipleOf':
        out = 'должен быть кратным ' + (e.schema);
        break;
      case 'not':
        out = 'должен не соответствовать схеме в "not"';
        break;
      case 'oneOf':
        out = 'должен соответствовать в точности одной схемe в "oneOf"';
        break;
      case 'pattern':
        out = 'должен соответствовать образцу "' + (e.params.escaped) + '"';
        break;
      case 'required':
        out = 'должен иметь обязательное поле ' + (e.params.missingProperty);
        break;
      case 'type':
        out = 'должен быть ' + (e.params.type);
        break;
      case 'uniqueItems':
        out = 'не должен иметь повторяющихся элементов (элементы ' + (e.params.j) + ' и ' + (e.params.i) + ' идентичны)';
        break;
    }
    e.message = out;
  }
};
